    public static int countTrees(int right, int down) {
        int trees = 0;
        int pos = 0;
        for (int row = down; row < INPUT.length; row += down) {
            pos = (pos + right) % (INPUT[row].length());
            if (INPUT[row].charAt(pos) == '#') {
                trees++;
            }
        }
        return trees;
    }
